CREATE TABLE IF NOT EXISTS `users` (
  `userID` int NOT NULL,
  `fullname` varchar(30) DEFAULT NULL,
  `username` varchar(25) DEFAULT NULL,
  `email` varchar(25) DEFAULT NULL,
  `password` varchar(72) DEFAULT NULL,
  PRIMARY KEY (`userID`),
  UNIQUE KEY `username` (`username`),
  UNIQUE KEY `email` (`email`)
);

CREATE TABLE IF NOT EXISTS `drivers` (
  `driverID` int NOT NULL,
  `years_of_experience` int DEFAULT NULL,
  `rating` decimal(2,1) DEFAULT NULL,
  `price_per_day` decimal(10,2) DEFAULT NULL,
  `preference` enum('2-Wheeler','4-Wheeler','Multi-Wheeler','None') DEFAULT NULL,
  `total_trips` int DEFAULT NULL,
  `name` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`driverID`)
);

CREATE TABLE IF NOT EXISTS `vehicles` (
  `vehicleID` int NOT NULL,
  `fuel_type` enum('Petrol','Diesel','Electric','Hybrid') DEFAULT NULL,
  `transmission` enum('Manual','Automatic') DEFAULT NULL,
  `seats` int DEFAULT NULL,
  `mileage` decimal(5,2) DEFAULT NULL,
  `price_per_day` decimal(10,2) DEFAULT NULL,
  `image_url` varchar(255) DEFAULT NULL,
  `type` enum('2-Wheeler','4-Wheeler','Multi-Wheeler') DEFAULT NULL,
  `name` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`vehicleID`)
);

CREATE TABLE IF NOT EXISTS `promotions` (
  `promoID` int NOT NULL,
  `promo_code` varchar(20) DEFAULT NULL,
  `description` varchar(100) DEFAULT NULL,
  `type` enum('Percentage','Flat') DEFAULT NULL,
  `value` decimal(10,2) DEFAULT NULL,
  `requirement` enum('Price','Days','Vehicle') DEFAULT NULL,
  `min_value` decimal(10,2) DEFAULT NULL,
  `active` tinyint(1) DEFAULT '1',
  `vehicle_req` enum('2-Wheeler','4-Wheeler','Multi-Wheeler') DEFAULT NULL,
  PRIMARY KEY (`promoID`)
);

CREATE TABLE IF NOT EXISTS `bookings` (
  `rideID` int NOT NULL,
  `userID` int DEFAULT NULL,
  `vehicleID` int DEFAULT NULL,
  `date` datetime DEFAULT NULL,
  `distance` decimal(10,2) DEFAULT NULL,
  `price` decimal(10,2) DEFAULT NULL,
  `days` int DEFAULT NULL,
  `driverID` int DEFAULT NULL,
  PRIMARY KEY (`rideID`),
  KEY `userID` (`userID`),
  KEY `vehicleID` (`vehicleID`),
  KEY `driverID` (`driverID`),
  CONSTRAINT `bookings_ibfk_1` FOREIGN KEY (`userID`) REFERENCES `users` (`userID`) ON DELETE CASCADE,
  CONSTRAINT `bookings_ibfk_2` FOREIGN KEY (`vehicleID`) REFERENCES `vehicles` (`vehicleID`),
  CONSTRAINT `bookings_ibfk_3` FOREIGN KEY (`driverID`) REFERENCES `drivers` (`driverID`) ON DELETE CASCADE ON UPDATE CASCADE
);

CREATE TABLE IF NOT EXISTS `leaderboard` (
  `rideID` int NOT NULL,
  `vehicleID` int NOT NULL,
  `rating` decimal(2,1) DEFAULT NULL,
  `review` varchar(100) DEFAULT NULL,
  `recommendation` int DEFAULT NULL,
  PRIMARY KEY (`rideID`,`vehicleID`),
  KEY `vehicleID` (`vehicleID`),
  CONSTRAINT `leaderboard_ibfk_1` FOREIGN KEY (`rideID`) REFERENCES `bookings` (`rideID`),
  CONSTRAINT `leaderboard_ibfk_2` FOREIGN KEY (`vehicleID`) REFERENCES `vehicles` (`vehicleID`),
  CONSTRAINT `leaderboard_chk_1` CHECK ((`rating` between 1 and 5)),
  CONSTRAINT `leaderboard_chk_2` CHECK ((`recommendation` between 1 and 10))
);



